    public function getSourceContext()
    {
        return new Source("{% extends \"base.html.twig\" %}

{% block body %}
 {%include \"_partials/_nav.html.twig\" %}
<div class=\"container\">
{# read and display just one flash message type #}
{% for message in app.flashes('success') %}
    <div class=\"alert alert-success mt-4\">
        {{message}}
    </div>
{% endfor %}

    <h1 class=\"mt-4\">Création d'un nouveau produit</h1>

   {{ form_start(form) }}
    <div class=\"form-error\">
        {{ form_errors(form) }}
    </div>

     <div class=\"form-group\">
      {{ form_label(form.name) }}
      {{ form_widget(form.name) }}
      <div class=\"form-error\">
        {{ form_errors(form.name) }}
      </div>  
    </div>

    <div class=\"form-group\">
      {{ form_label(form.price) }}
      {{ form_widget(form.price) }}
      <div class=\"form-error\">
        {{ form_errors(form.price) }}
      </div>  
    </div>

    <div class=\"form-group\">
      {{ form_label(form.description) }}
      {{ form_widget(form.description) }}
      <div class=\"form-error\">
        {{ form_errors(form.description) }}
      </div>  
    </div>

    <div class=\"form-group\">
      {{ form_label(form.inStock )}}
      {{ form_widget(form.inStock) }}
     <div class=\"form-error\">
        {{ form_errors(form.inStock) }}
      </div>  
    </div>

    <div class=\"form-group\">
      {{ form_row(form.submit) }}
    </div>

{{ form_end(form) }}
</div>

{% endblock %}", "product/newprod.html.twig", "C:\\xampp\\htdocs\\EcommerceTP\\templates\\product\\newprod.html.twig");
